mapper.select:
SELECT *
FROM file
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}
  AND file_code != 1
ORDER BY reg_date DESC

SELECT *
FROM file
WHERE parent_table = #{parentTable}
  AND parent_no = #{recruitNo}
  AND file_code = 1

SELECT *
FROM file
WHERE parent_table = #{parentTable}
AND parent_no = #{cvNo}
AND file_code = 1

mapper.update:
UPDATE file
   SET parent_table = #{parentTable}
      ,parent_no = #{parentNo}
      ,file_name = #{fileName}
      ,origin_name = #{originName}
      ,file_path = #{filePath}
      ,file_size = #{fileSize}
      ,file_code = #{fileCode}
WHERE file_no = #{fileNo}

mapper.delete:
DELETE FROM file
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}
